import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, artistAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking({ user }) {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  
  const [artwork, setArtwork] = useState(null);
  const [artist, setArtist] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    customer_name: user?.full_name || user?.username || '',
    email: user?.email || '',
    phone: '',
    preferred_date: '',
    preferred_time: '',
    delivery_address: '',
    special_instructions: ''
  });

  useEffect(() => {
    const fetchArtworkDetails = async () => {
      try {
        setLoading(true);
        const artworkResponse = await artworkAPI.getArtworkById(artworkId);
        const artworkData = artworkResponse.data.data;
        setArtwork(artworkData);
        
        const artistResponse = await artistAPI.getArtistById(artworkData.artist_id);
        setArtist(artistResponse.data.data);
        
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artwork:', error);
        setLoading(false);
      }
    };

    fetchArtworkDetails();
  }, [artworkId]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    // Simulate booking submission (will connect to backend later)
    setTimeout(() => {
      console.log('Booking submitted:', {
        ...formData,
        artwork_id: artwork.artwork_id,
        artwork_title: artwork.title,
        price: artwork.price,
        user_id: user?.user_id
      });
      
      setSubmitting(false);
      setShowSuccess(true);
      
      // Redirect to home after 3 seconds
      setTimeout(() => {
        navigate('/');
      }, 3000);
    }, 1500);
  };

  if (loading) {
    return (
      <div className="booking-loading">
        <div className="spinner"></div>
        <p>Loading booking details...</p>
      </div>
    );
  }

  if (!artwork) {
    return (
      <div className="booking-error">
        <h2>Artwork not found</h2>
        <button onClick={() => navigate('/')}>Back to Home</button>
      </div>
    );
  }

  if (showSuccess) {
    return (
      <div className="booking-success-container">
        <div className="success-card">
          <div className="success-icon">✓</div>
          <h1>Booking Confirmed!</h1>
          <p>Your booking request has been submitted successfully.</p>
          <div className="success-details">
            <p><strong>Artwork:</strong> {artwork.title}</p>
            <p><strong>Artist:</strong> {artist?.artist_name}</p>
            <p><strong>Date:</strong> {formData.preferred_date}</p>
            <p><strong>Time:</strong> {formData.preferred_time}</p>
          </div>
          <p className="redirect-message">Redirecting to home...</p>
        </div>
      </div>
    );
  }

  // Get today's date for min attribute
  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="booking-container">
      <button className="back-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <div className="booking-content">
        {/* Artwork Preview Section */}
        <div className="artwork-preview">
          <h2>Artwork Details</h2>
          <div className="preview-card">
            <img 
              src={getImageURL(artwork.image_url)} 
              alt={artwork.title}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x500?text=Image+Not+Found';
              }}
            />
            <div className="preview-info">
              <h3>{artwork.title}</h3>
              <p className="preview-artist">by {artist?.artist_name}</p>
              <p className="preview-category">{artwork.category}</p>
              <p className="preview-size">Wall Size: {artwork.wall_size}</p>
              <div className="preview-price">
                NPR {artwork.price.toLocaleString()}
              </div>
            </div>
          </div>
        </div>

        {/* Booking Form Section */}
        <div className="booking-form-section">
          <h2>Book This Artwork</h2>
          <p className="form-subtitle">Fill in your details to complete the booking</p>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-group">
              <label htmlFor="customer_name">Full Name *</label>
              <input
                type="text"
                id="customer_name"
                name="customer_name"
                value={formData.customer_name}
                onChange={handleChange}
                required
                placeholder="Enter your full name"
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="email">Email Address *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  placeholder="your.email@example.com"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number *</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                  placeholder="+977-XXXXXXXXXX"
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="preferred_date">Preferred Date *</label>
                <input
                  type="date"
                  id="preferred_date"
                  name="preferred_date"
                  value={formData.preferred_date}
                  onChange={handleChange}
                  required
                  min={today}
                />
              </div>

              <div className="form-group">
                <label htmlFor="preferred_time">Preferred Time *</label>
                <input
                  type="time"
                  id="preferred_time"
                  name="preferred_time"
                  value={formData.preferred_time}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="delivery_address">Delivery Address *</label>
              <textarea
                id="delivery_address"
                name="delivery_address"
                value={formData.delivery_address}
                onChange={handleChange}
                required
                rows="3"
                placeholder="Enter your complete delivery address"
              />
            </div>

            <div className="form-group">
              <label htmlFor="special_instructions">Special Instructions (Optional)</label>
              <textarea
                id="special_instructions"
                name="special_instructions"
                value={formData.special_instructions}
                onChange={handleChange}
                rows="3"
                placeholder="Any special requests or notes for the artist..."
              />
            </div>

            <div className="booking-summary">
              <h3>Booking Summary</h3>
              <div className="summary-item">
                <span>Artwork Price:</span>
                <span>NPR {artwork.price.toLocaleString()}</span>
              </div>
              <div className="summary-item">
                <span>Service Fee:</span>
                <span>NPR 500</span>
              </div>
              <div className="summary-item total">
                <span>Total Amount:</span>
                <span>NPR {(artwork.price + 500).toLocaleString()}</span>
              </div>
            </div>

            <button 
              type="submit" 
              className="submit-booking-btn"
              disabled={submitting}
            >
              {submitting ? 'Processing...' : '✓ Confirm Booking'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
