import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { bookingAPI, getImageURL } from '../../services/api';
import './MyBookings.css';

function MyBookings({ user }) {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(''); // Yellow warning was here, now fixed by using it below

  const isAdmin = user?.role === 'admin' || user?.email === 'admin@gmail.com';

  useEffect(() => {
    if (!user) {
      setError('Please login to view bookings');
      setLoading(false);
      return;
    }
    fetchBookings();
  }, [user]);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      setError(''); // Clear previous errors
      const response = await bookingAPI.getUserBookings();
      
      // Handle array or success object response
      if (Array.isArray(response.data)) {
        setBookings(response.data);
      } else if (response.data.success) {
        setBookings(response.data.bookings || []);
      } else {
        setError('Failed to fetch bookings. Please try again later.');
      }
    } catch (err) {
      setError('Connection error. Please check if the backend is running.');
      console.error('Booking fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch(status?.toLowerCase()) {
      case 'pending': return 'status-pending';
      case 'confirmed': return 'status-confirmed';
      case 'completed': return 'status-completed';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-default';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric', month: 'short', day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner"><div className="spinner"></div><p>Loading...</p></div>
      </div>
    );
  }

  // --- DETAIL VIEW ---
  if (selectedBooking) {
    return (
      <div className="my-bookings-container">
        <div className="bookings-wrapper">
          <button onClick={() => setSelectedBooking(null)} className="btn-back-to-list">
            ‚Üê Back to {isAdmin ? 'All Bookings' : 'My Bookings'}
          </button>
          <div className="booking-detail-card">
            <div className="booking-detail-inner">
              <div className="booking-detail-image">
                <img 
                  src={getImageURL(selectedBooking.image_url)} 
                  alt={selectedBooking.artwork_title}
                  onError={(e) => { e.target.src = 'https://via.placeholder.com/400x500?text=No+Image'; }}
                />
              </div>
              <div className="booking-detail-content">
                <div className="booking-header-row">
                  <div>
                    <h1 className="booking-detail-title">{selectedBooking.artwork_title}</h1>
                    <p className="booking-id">Booking ID: #{selectedBooking.id}</p>
                  </div>
                  <span className={`booking-status ${getStatusColor(selectedBooking.status)}`}>
                    {selectedBooking.status?.toUpperCase()}
                  </span>
                </div>
                <div className="info-section">
                  <h3>üë§ Customer Details</h3>
                  <p><strong>Name:</strong> {selectedBooking.customer_name}</p>
                  <p><strong>Email:</strong> {selectedBooking.email}</p>
                  <p><strong>Phone:</strong> {selectedBooking.phone}</p>
                  <p><strong>Address:</strong> {selectedBooking.delivery_address}</p>
                </div>
                <div className="booking-footer">
                  <div className="booking-amount">
                    <span className="amount-label">Total Amount</span>
                    <span className="amount-value">NPR {Number(selectedBooking.total_amount).toLocaleString()}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // --- LIST VIEW ---
  return (
    <div className="my-bookings-container">
      <div className="bookings-wrapper">
        <div className="bookings-header">
          <h1>{isAdmin ? 'Customer Bookings' : 'My Bookings'}</h1>
          <p>{isAdmin ? 'Admin Dashboard' : `Welcome, ${user?.name}`}</p>
        </div>

        {/* ERROR DISPLAY - This fixes the yellow warning */}
        {error && (
          <div className="error-alert">
            <span>‚ö†Ô∏è</span> {error}
            <button onClick={fetchBookings} className="retry-btn">Retry</button>
          </div>
        )}

        {bookings.length === 0 && !error ? (
          <div className="no-bookings">
            <p>No bookings found.</p>
            <button onClick={() => navigate('/')} className="btn-primary">Browse Artworks</button>
          </div>
        ) : (
          <div className="bookings-list-compact">
            {bookings.map((booking) => (
              <div key={booking.id} className="booking-list-item-compact" onClick={() => setSelectedBooking(booking)}>
                <img className="list-thumb" src={getImageURL(booking.image_url)} alt="" onError={(e) => { e.target.src = 'https://via.placeholder.com/50'; }} />
                <div className="list-info-main">
                  <h4 className="list-title">{booking.artwork_title}</h4>
                  <p className="list-subtext">{isAdmin ? `Customer: ${booking.customer_name}` : `ID: #${booking.id}`}</p>
                </div>
                <div className="list-meta">
                  <span className="list-date">{formatDate(booking.created_at)}</span>
                  <span className={`list-status-pill ${getStatusColor(booking.status)}`}>{booking.status}</span>
                </div>
                <div className="list-price">NPR {Number(booking.total_amount).toLocaleString()}</div>
                <div className="list-arrow">‚Ä∫</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MyBookings;
