import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, artistAPI, getImageURL } from '../../services/api';
import './ArtworkDetail.css';

function ArtworkDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [artwork, setArtwork] = useState(null);
  const [artist, setArtist] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchArtworkDetails = async () => {
      try {
        setLoading(true);
        
        // Redirect if trying to access artwork_id 20
        if (parseInt(id) === 20) {
          navigate('/');
          return;
        }
        
        const artworkResponse = await artworkAPI.getArtworkById(id);
        const artworkData = artworkResponse.data.data;
        
        // Double-check and redirect if artwork_id is 20
        if (artworkData && artworkData.artwork_id === 20) {
          navigate('/');
          return;
        }
        
        setArtwork(artworkData);
        
        const artistResponse = await artistAPI.getArtistById(artworkData.artist_id);
        setArtist(artistResponse.data.data);
        
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artwork details:', error);
        setLoading(false);
      }
    };

    fetchArtworkDetails();
  }, [id, navigate]);

  // UPDATED: Handle Book Now button click with login check
  const handleBookNow = () => {
    // Check if user is logged in
    const user = localStorage.getItem('user');
    
    if (user) {
      // User is logged in, go directly to booking
      navigate(`/booking/${artwork.artwork_id}`);
    } else {
      // User not logged in, redirect to login and save booking page path
      navigate('/login', { 
        state: { from: `/booking/${artwork.artwork_id}` } 
      });
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading artwork details...</p>
      </div>
    );
  }

  if (!artwork) {
    return (
      <div className="error-container">
        <h2>Artwork not found</h2>
        <button onClick={() => navigate('/')}>Back to Home</button>
      </div>
    );
  }

  return (
    <div className="artwork-detail-container">
      <button className="back-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <div className="detail-content">
        <div className="image-section">
          <img 
            src={getImageURL(artwork.image_url)} 
            alt={artwork.title}
            onError={(e) => {
              e.target.src = 'https://via.placeholder.com/600x800?text=Image+Not+Found';
            }}
          />
          {artwork.is_bestseller && (
            <span className="bestseller-badge-large">‚≠ê Best Seller</span>
          )}
        </div>

        <div className="info-section">
          <div className="category-badge">{artwork.category}</div>
          
          <h1>{artwork.title}</h1>
          
          <div className="price-tag">
            NPR {artwork.price.toLocaleString()}
          </div>

          <div className="specs">
            <div className="spec-item">
              <span className="spec-label">Wall Size:</span>
              <span className="spec-value">{artwork.wall_size}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Views:</span>
              <span className="spec-value">{artwork.views.toLocaleString()}</span>
            </div>
          </div>

          <div className="description">
            <h3>About this artwork</h3>
            <p>{artwork.description}</p>
          </div>

          {artist && (
            <div className="artist-card">
              <div className="artist-card-header">
                <div>
                  <h3>Created by</h3>
                  <h2>{artist.artist_name}</h2>
                  <p>{artist.bio}</p>
                </div>
                <button 
                  className="view-artist-btn"
                  onClick={() => navigate(`/artist/${artist.artist_id}`)}
                >
                  View Profile ‚Üí
                </button>
              </div>
              
              <div className="artist-contact">
                <div className="contact-item">
                  <span className="contact-icon">üìß</span>
                  <span>{artist.contact_email}</span>
                </div>
                <div className="contact-item">
                  <span className="contact-icon">üì±</span>
                  <span>{artist.phone}</span>
                </div>
              </div>
            </div>
          )}

          {/* Book Now button - checks login and saves redirect path */}
          <button className="btn-book-now" onClick={handleBookNow}>
            üé® Book Now
          </button>
        </div>
      </div>

      {artist && artist.artworks && artist.artworks.length > 1 && (
        <div className="more-artworks">
          <h2>More from {artist.artist_name}</h2>
          <div className="artworks-grid">
            {artist.artworks
              .filter(art => art.artwork_id !== parseInt(id) && art.artwork_id !== 20)
              .slice(0, 4)
              .map(art => (
                <div 
                  key={art.artwork_id}
                  className="artwork-card-small"
                  onClick={() => navigate(`/artwork/${art.artwork_id}`)}
                >
                  <img src={getImageURL(art.image_url)} alt={art.title} />
                  <div className="card-info">
                    <h4>{art.title}</h4>
                    <p>NPR {art.price.toLocaleString()}</p>
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ArtworkDetail;
