import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, artistAPI, bookingAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking({ user }) {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  
  // Artwork and artist state
  const [artwork, setArtwork] = useState(null);
  const [artist, setArtist] = useState(null);
  const [pageLoading, setPageLoading] = useState(true);
  const [showSuccess, setShowSuccess] = useState(false);
  
  // Form state
  const [formData, setFormData] = useState({
    customer_name: '',
    email: '',
    phone: '',
    delivery_address: '',
    preferred_date: '',
    preferred_time: '',
    special_instructions: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // 1. AUTO-FILL FIX: Update form when 'user' prop becomes available
  useEffect(() => {
    if (user) {
      setFormData(prev => ({
        ...prev,
        customer_name: user.full_name || user.username || prev.customer_name,
        email: user.email || prev.email,
        phone: user.phone || prev.phone
      }));
    }
  }, [user]);

  // Fetch artwork details from API
  useEffect(() => {
    const fetchArtworkDetails = async () => {
      try {
        setPageLoading(true);
        
        // Fetch artwork
        const artworkResponse = await artworkAPI.getArtworkById(artworkId);
        const artworkData = artworkResponse.data.data;
        setArtwork(artworkData);
        
        // Fetch artist
        const artistResponse = await artistAPI.getArtistById(artworkData.artist_id);
        setArtist(artistResponse.data.data);
        
        setPageLoading(false);
      } catch (error) {
        console.error('Error fetching artwork details:', error);
        setError('Failed to load artwork details');
        setPageLoading(false);
      }
    };

    fetchArtworkDetails();
  }, [artworkId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Validate form
      if (!formData.customer_name || !formData.email || !formData.phone || 
          !formData.delivery_address || !formData.preferred_date || !formData.preferred_time) {
        setError('Please fill in all required fields');
        setLoading(false);
        return;
      }

      // Create booking object for backend
      const bookingData = {
        artwork_id: artwork.artwork_id,
        customer_name: formData.customer_name,
        email: formData.email,
        phone: formData.phone,
        delivery_address: formData.delivery_address,
        preferred_date: formData.preferred_date,
        preferred_time: formData.preferred_time,
        special_instructions: formData.special_instructions,
        total_amount: artwork.price
      };

      // Save to backend (PostgreSQL)
      const response = await bookingAPI.createBooking(bookingData);
      
      if (response.data.success) {
        // Show success screen
        setShowSuccess(true);
        setLoading(false);

        // Redirect to My Bookings after 3 seconds
        setTimeout(() => {
          navigate('/my-bookings');
        }, 3000);
      } else {
        setError(response.data.message || 'Failed to create booking');
        setLoading(false);
      }

    } catch (err) {
      console.error('Booking error:', err);
      // More detailed error message for debugging
      const errorMsg = err.response?.data?.message || err.message || 'Failed to create booking.';
      setError(errorMsg);
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (timeString) => {
    return timeString.slice(0, 5); // HH:MM format
  };

  // Show success screen after booking
  if (showSuccess) {
    return (
      <div className="booking-success-container">
        <div className="success-card">
          <div className="success-icon">‚úì</div>
          <h1>Booking Successful!</h1>
          <p>Your booking has been confirmed</p>
          
          <div className="success-details">
            <p><strong>Artwork:</strong> {artwork?.title}</p>
            <p><strong>Customer:</strong> {formData.customer_name}</p>
            <p><strong>Delivery Date:</strong> {formatDate(formData.preferred_date)}</p>
            <p><strong>Time:</strong> {formatTime(formData.preferred_time)}</p>
            <p><strong>Total:</strong> NPR {artwork?.price.toLocaleString()}</p>
          </div>
          
          <p className="redirect-message">Redirecting to My Bookings in 3 seconds...</p>
        </div>
      </div>
    );
  }

  if (pageLoading) {
    return (
      <div className="booking-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading artwork details...</p>
        </div>
      </div>
    );
  }

  if (error && !artwork) {
    return (
      <div className="booking-container">
        <div className="booking-error">
          <h2>Error Loading Artwork</h2>
          <p>{error}</p>
          <button onClick={() => navigate('/')}>Back to Home</button>
        </div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <button className="back-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <div className="booking-content">
        {/* Left Side - Artwork Details */}
        <div className="artwork-preview">
          <h2>Artwork Details</h2>
          <div className="preview-card">
            <img 
              src={getImageURL(artwork.image_url)} 
              alt={artwork.title}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x500?text=Image+Not+Found';
              }}
            />
            <div className="preview-info">
              <h3>{artwork.title}</h3>
              <p className="preview-artist">by {artist?.artist_name || 'Unknown Artist'}</p>
              <span className="preview-category">{artwork.category}</span>
              <p className="preview-size">{artwork.wall_size}</p>
              <div className="preview-price">NPR {artwork.price.toLocaleString()}</div>
            </div>
          </div>
        </div>

        {/* Right Side - Booking Form */}
        <div className="booking-form-section">
          <h2>Complete Your Booking</h2>
          <p className="form-subtitle">Fill in your details to book this artwork</p>
          
          {error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="booking-form">
            {/* Customer Details */}
            <div className="form-section">
              <h3>Customer Details</h3>
              
              <div className="form-group">
                <label htmlFor="customer_name">Full Name *</label>
                <input
                  type="text"
                  id="customer_name"
                  name="customer_name"
                  value={formData.customer_name}
                  onChange={handleChange}
                  required
                  placeholder="Enter your full name"
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  placeholder="your.email@example.com"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number *</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                  placeholder="+977 9800000000"
                />
              </div>
            </div>

            {/* Delivery Details */}
            <div className="form-section">
              <h3>Delivery Details</h3>
              
              <div className="form-group">
                <label htmlFor="delivery_address">Delivery Address *</label>
                <textarea
                  id="delivery_address"
                  name="delivery_address"
                  value={formData.delivery_address}
                  onChange={handleChange}
                  required
                  rows="3"
                  placeholder="Enter your complete delivery address"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="preferred_date">Preferred Date *</label>
                  <input
                    type="date"
                    id="preferred_date"
                    name="preferred_date"
                    value={formData.preferred_date}
                    onChange={handleChange}
                    required
                    min={new Date().toISOString().split('T')[0]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="preferred_time">Preferred Time *</label>
                  <input
                    type="time"
                    id="preferred_time"
                    name="preferred_time"
                    value={formData.preferred_time}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>

            {/* Special Instructions */}
            <div className="form-section">
              <h3>Special Instructions (Optional)</h3>
              
              <div className="form-group">
                <label htmlFor="special_instructions">Additional Notes</label>
                <textarea
                  id="special_instructions"
                  name="special_instructions"
                  value={formData.special_instructions}
                  onChange={handleChange}
                  rows="4"
                  placeholder="Any special requirements or instructions for delivery..."
                />
              </div>
            </div>

            {/* Booking Summary */}
            <div className="booking-summary">
              <h3>Booking Summary</h3>
              <div className="summary-item">
                <span>Artwork</span>
                <span>{artwork?.title}</span>
              </div>
              <div className="summary-item">
                <span>Artist</span>
                <span>{artist?.artist_name || 'Unknown'}</span>
              </div>
              <div className="summary-item">
                <span>Size</span>
                <span>{artwork?.wall_size}</span>
              </div>
              <div className="summary-item total">
                <span>Total Amount</span>
                <span>NPR {artwork?.price.toLocaleString()}</span>
              </div>
            </div>

            {/* Submit Button */}
            <button 
              type="submit" 
              className="submit-booking-btn"
              disabled={loading}
            >
              {loading ? 'Processing...' : 'üé® Book Now'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
