import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, bookingAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking() {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  const [artwork, setArtwork] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    bookingDate: '',
    bookingTime: '',
    address: '',
    city: '',
    notes: ''
  });

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        setLoading(true);
        const response = await artworkAPI.getArtworkById(artworkId);
        setArtwork(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artwork:', error);
        setError('Failed to load artwork');
        setLoading(false);
      }
    };

    if (artworkId) {
      fetchArtwork();
    }
  }, [artworkId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError('');
  };

  const validateForm = () => {
    if (!formData.fullName.trim()) { setError('Please enter your full name'); return false; }
    if (!formData.email.trim() || !formData.email.includes('@')) { setError('Please enter a valid email address'); return false; }
    if (!formData.phone.trim()) { setError('Please enter your phone number'); return false; }
    if (!formData.bookingDate) { setError('Please select a booking date'); return false; }
    if (!formData.bookingTime) { setError('Please select a booking time'); return false; }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setSubmitting(true);
    setError('');

    try {
      // Get User ID from LocalStorage
      const userStr = localStorage.getItem('user');
      const userData = JSON.parse(userStr);
      // Support multiple data structures for the user ID
      const userId = userData?.user?.user_id || userData?.user?.id || userData?.id;

      if (!userId) {
        throw new Error("User not found. Please log in again.");
      }

      const bookingData = {
        artwork_id: parseInt(artwork.artwork_id), // Ensure it's an integer
        customer_name: formData.fullName,
        email: formData.email,
        phone: formData.phone,
        preferred_date: formData.bookingDate,
        preferred_time: formData.bookingTime,
        delivery_address: `${formData.address}${formData.city ? ', ' + formData.city : ''}`,
        special_instructions: formData.notes,
        total_amount: parseFloat(artwork.price) // Ensure it's a number
      };

      const response = await bookingAPI.createBooking(bookingData);
      
      if (response.data.success) {
        setSuccess(true);
        setTimeout(() => {
          navigate('/my-bookings');
        }, 2000);
      } else {
        throw new Error(response.data.message || 'Booking failed');
      }
    } catch (err) {
      console.error('❌ Booking error:', err);
      setError(err.response?.data?.message || err.message || 'Failed to submit booking');
      setSubmitting(false);
    }
  };

  if (loading) return <div className="loading-container"><div className="spinner"></div><p>Loading...</p></div>;
  if (!artwork) return <div className="error-container"><h2>No Artwork Selected</h2><button onClick={() => navigate('/')}>Home</button></div>;

  if (success) {
    return (
      <div className="success-container">
        <div className="success-icon">✓</div>
        <h2>Booking Confirmed!</h2>
        <p>Your consultation for <strong>{artwork.title}</strong> is booked.</p>
        <p>Redirecting to My Bookings...</p>
      </div>
    );
  }

  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="booking-container">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>
      <div className="booking-content">
        <div className="booking-summary">
          <h2>Summary</h2>
          <div className="summary-artwork">
            <img src={getImageURL(artwork.image_url)} alt={artwork.title} className="summary-image" />
            <div className="summary-info">
              <h3>{artwork.title}</h3>
              <p>NPR {Number(artwork.price).toLocaleString()}</p>
            </div>
          </div>
        </div>

        <div className="booking-form-section">
          <h2>Your Details</h2>
          {error && <div className="error-message">{error}</div>}
          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-group">
              <label>Full Name *</label>
              <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} required />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Email *</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />
              </div>
              <div className="form-group">
                <label>Phone *</label>
                <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Date *</label>
                <input type="date" name="bookingDate" value={formData.bookingDate} onChange={handleChange} min={today} required />
              </div>
              <div className="form-group">
                <label>Time *</label>
                <input type="time" name="bookingTime" value={formData.bookingTime} onChange={handleChange} required />
              </div>
            </div>
            <div className="form-group">
              <label>Notes</label>
              <textarea name="notes" value={formData.notes} onChange={handleChange} rows="3"></textarea>
            </div>
            <button type="submit" className="submit-booking-btn" disabled={submitting}>
              {submitting ? 'Processing...' : 'Confirm Booking'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
