// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useSearchParams } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar';
import Home from './components/Home/Home';
import ArtworkDetail from './components/ArtworkDetail/ArtworkDetail';
import ArtistProfile from './components/ArtistProfile/ArtistProfile';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';
import Booking from './components/Booking/Booking';
import MyBookings from './components/MyBookings/MyBookings';
import AdminBookings from './components/AdminBookings/AdminBookings';
import ManageContent from './components/ManageContent/ManageContent';
import ScrollToTop from './components/shared/ScrollToTop';
import SplashScreen from './components/SplashScreen/SplashScreen';
import Footer from './components/Footer/Footer';

import './App.css';

// ‚≠ê NEW: Wrapper component to handle redirect for authenticated users
function SignupRoute({ isAuthenticated, setIsAuthenticated, setUser }) {
  const [searchParams] = useSearchParams();
  const redirect = searchParams.get('redirect');

  if (isAuthenticated) {
    // If already authenticated and there's a redirect, go there instead of home
    return <Navigate to={redirect || '/'} replace />;
  }

  return <Signup setIsAuthenticated={setIsAuthenticated} setUser={setUser} />;
}

// ‚≠ê NEW: Wrapper component to handle redirect for authenticated users
function LoginRoute({ isAuthenticated, setIsAuthenticated, setUser }) {
  const [searchParams] = useSearchParams();
  const redirect = searchParams.get('redirect');

  if (isAuthenticated) {
    // If already authenticated and there's a redirect, go there instead of home
    return <Navigate to={redirect || '/'} replace />;
  }

  return <Login setIsAuthenticated={setIsAuthenticated} setUser={setUser} isAuthenticated={isAuthenticated} />;
}

function App() {
  const [showSplash, setShowSplash] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);

  useEffect(() => {
    const checkAuth = () => {
      console.log('üîç Checking authentication status...');
      
      // ‚≠ê ADDED: Clear localStorage on app open - NO AUTO-LOGIN
      localStorage.removeItem('user');
      console.log('üßπ Cleared any existing session - manual login required');
      
      setUser(null);
      setIsAuthenticated(false);
      setLoadingUser(false);
      
      console.log('‚ÑπÔ∏è No user found in localStorage');
    };
    
    checkAuth();
  }, []);

  const handleLogout = () => {
    console.log('üëã Logging out user...');
    console.trace('üîç Logout called from:'); // ‚≠ê This will show WHERE logout is being called from
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  const handleSplashFinish = () => {
    setShowSplash(false);
  };

  const ProtectedRoute = ({ children }) => {
    const location = useLocation();
    
    if (loadingUser) {
      return (
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh' 
        }}>
          Loading...
        </div>
      );
    }
    
    if (!isAuthenticated) {
      console.log('üö´ Access denied - redirecting to login');
      console.log('üìç Saving redirect path:', location.pathname);
      return <Navigate to={`/login?redirect=${encodeURIComponent(location.pathname)}`} replace />;
    }
    
    return children;
  };

  if (showSplash) {
    return <SplashScreen onFinish={handleSplashFinish} />;
  }

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="App">
        <ScrollToTop />

        <Navbar
          isAuthenticated={isAuthenticated}
          user={user}
          onLogout={handleLogout}
        />

        <Routes>
          <Route
            path="/login"
            element={
              <LoginRoute 
                isAuthenticated={isAuthenticated}
                setIsAuthenticated={setIsAuthenticated}
                setUser={setUser}
              />
            }
          />
          
          <Route
            path="/signup"
            element={
              <SignupRoute 
                isAuthenticated={isAuthenticated}
                setIsAuthenticated={setIsAuthenticated}
                setUser={setUser}
              />
            }
          />
          
          <Route path="/" element={<Home />} />
          <Route path="/artwork/:id" element={<ArtworkDetail />} />
          <Route path="/artist/:id" element={<ArtistProfile />} />

          <Route
            path="/booking/:artworkId"
            element={
              <ProtectedRoute>
                <Booking user={user} />
              </ProtectedRoute>
            }
          />

          <Route
            path="/my-bookings"
            element={
              <ProtectedRoute>
                <MyBookings user={user} />
              </ProtectedRoute>
            }
          />

          <Route
            path="/admin/bookings"
            element={
              <ProtectedRoute>
                <AdminBookings user={user} />
              </ProtectedRoute>
            }
          />

          <Route
            path="/admin/manage-content"
            element={
              <ProtectedRoute>
                <ManageContent />
              </ProtectedRoute>
            }
          />

          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>

        <Footer isAuthenticated={isAuthenticated} user={user} />
      </div>
    </Router>
  );
}

export default App;
