import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import Home from './components/Home/Home';
import ArtworkDetail from './components/ArtworkDetail/ArtworkDetail';
import ArtistProfile from './components/ArtistProfile/ArtistProfile';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';
import './App.css';

function App() {
  // Simulate authentication state (we'll connect to backend later)
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  // Check if user is logged in on component mount
  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      setUser(JSON.parse(loggedInUser));
      setIsAuthenticated(true);
    }
  }, []);

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  // Protected Route Component
  const ProtectedRoute = ({ children }) => {
    if (!isAuthenticated) {
      return <Navigate to="/login" replace />;
    }
    return children;
  };

  // Public Route Component (redirects to home if already logged in)
  const PublicRoute = ({ children }) => {
    if (isAuthenticated) {
      return <Navigate to="/" replace />;
    }
    return children;
  };

  return (
    <Router>
      <div className="App">
        {/* Show Navbar only when authenticated */}
        {isAuthenticated && (
          <Navbar 
            isAuthenticated={isAuthenticated} 
            user={user} 
            onLogout={handleLogout} 
          />
        )}
        
        <Routes>
          {/* Public Routes - Login & Signup */}
          <Route 
            path="/login" 
            element={
              <PublicRoute>
                <Login setIsAuthenticated={setIsAuthenticated} setUser={setUser} />
              </PublicRoute>
            } 
          />
          <Route 
            path="/signup" 
            element={
              <PublicRoute>
                <Signup setIsAuthenticated={setIsAuthenticated} setUser={setUser} />
              </PublicRoute>
            } 
          />
          
          {/* Protected Routes - Require Login */}
          <Route 
            path="/" 
            element={
              <ProtectedRoute>
                <Home />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/artwork/:id" 
            element={
              <ProtectedRoute>
                <ArtworkDetail />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/artist/:id" 
            element={
              <ProtectedRoute>
                <ArtistProfile />
              </ProtectedRoute>
            } 
          />

          {/* Fallback - redirect to home or login */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
