import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, bookingAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking() {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  const [artwork, setArtwork] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    bookingDate: '',
    bookingTime: '',
    address: '',
    city: '',
    notes: ''
  });

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        setLoading(true);
        console.log('Fetching artwork with ID:', artworkId);
        const response = await artworkAPI.getArtworkById(artworkId);
        setArtwork(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artwork:', error);
        setError('Failed to load artwork');
        setLoading(false);
      }
    };

    if (artworkId) {
      fetchArtwork();
    }
  }, [artworkId]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    if (error) setError('');
  };

  const validateForm = () => {
    if (!formData.fullName.trim()) {
      setError('Please enter your full name');
      return false;
    }
    if (!formData.email.trim() || !formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.phone.trim()) {
      setError('Please enter your phone number');
      return false;
    }
    if (!formData.bookingDate) {
      setError('Please select a booking date');
      return false;
    }
    if (!formData.bookingTime) {
      setError('Please select a booking time');
      return false;
    }
    return true;
  };

  // ‚úÖ UPDATED: handleSubmit now uses backend API (NO localStorage)
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setSubmitting(true);
    setError('');

    try {
      console.log('üíæ Starting to save booking...');
      console.log('üì¶ Artwork data:', artwork);

      const bookingData = {
        artwork_id: artwork.artwork_id,
        customer_name: formData.fullName,
        email: formData.email,
        phone: formData.phone,
        preferred_date: formData.bookingDate,
        preferred_time: formData.bookingTime,
        delivery_address: formData.address
          ? `${formData.address}, ${formData.city}`
          : formData.city || '',
        special_instructions: formData.notes,
        total_amount: artwork.price
      };

      console.log('üìã Sending booking data to backend:', bookingData);

      const response = await bookingAPI.createBooking(bookingData);
      
      console.log('‚úÖ Booking created successfully:', response.data);

      setSuccess(true);
      
      setTimeout(() => {
        console.log('üîÄ Redirecting to my-bookings...');
        navigate('/my-bookings');
      }, 3000);

    } catch (err) {
      console.error('‚ùå Booking error:', err);
      console.error('‚ùå Error response:', err.response?.data);
      setError(
        err.response?.data?.message ||
        'Failed to submit booking. Please try again.'
      );
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  if (!artwork) {
    return (
      <div className="error-container">
        <div className="error-icon">‚ö†Ô∏è</div>
        <h2>No Artwork Selected</h2>
        <p>Please select an artwork before booking.</p>
        <button className="home-btn" onClick={() => navigate('/')}>
          Go Back Home
        </button>
      </div>
    );
  }

  if (success) {
    return (
      <div className="success-container">
        <div className="success-animation">
          <div className="success-icon">‚úì</div>
          <div className="success-circle"></div>
        </div>
        <h2>Booking Confirmed!</h2>
        <p className="success-message">
          Your consultation with <strong>{artwork.artist_name}</strong> has been booked successfully.
        </p>
        <div className="success-details">
          <p>üìÖ Date: <strong>{formData.bookingDate}</strong></p>
          <p>üïê Time: <strong>{formData.bookingTime}</strong></p>
        </div>
        <p className="success-note">
          The artist will contact you at <strong>{formData.email}</strong> to confirm the details.
        </p>
        <div className="redirect-progress">
          <div className="progress-bar"></div>
        </div>
        <p className="redirect-text">Redirecting to My Bookings...</p>
      </div>
    );
  }

  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="booking-container">
      {/* üîπ REST OF YOUR FILE REMAINS 100% UNCHANGED üîπ */}
      {/* (Form JSX, UI, styles, notices, etc.) */}
    </div>
  );
}

export default Booking;
