import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, bookingAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking() {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  const [artwork, setArtwork] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    bookingDate: '',
    bookingTime: '',
    address: '',
    city: '',
    postalCode: ''
  });

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        setLoading(true);
        console.log('Fetching artwork with ID:', artworkId);
        const response = await artworkAPI.getArtworkById(artworkId);
        setArtwork(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artwork:', error);
        setError('Failed to load artwork');
        setLoading(false);
      }
    };

    if (artworkId) {
      fetchArtwork();
    }
  }, [artworkId]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    if (error) setError('');
  };

  const validateForm = () => {
    if (!formData.fullName.trim()) {
      setError('Please enter your full name');
      return false;
    }
    if (!formData.email.trim() || !formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.phone.trim()) {
      setError('Please enter your phone number');
      return false;
    }
    if (!formData.bookingDate) {
      setError('Please select a booking date');
      return false;
    }
    if (!formData.bookingTime) {
      setError('Please select a booking time');
      return false;
    }
    if (!formData.address.trim()) {
      setError('Please enter your address');
      return false;
    }
    if (!formData.city.trim()) {
      setError('Please enter your city');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setSubmitting(true);
    setError('');

    try {
      console.log('üíæ Submitting booking to backend...');

      // ‚úÖ FIXED: Get user_id from localStorage correctly
      const userData = JSON.parse(localStorage.getItem('user'));
      console.log('üë§ User data from localStorage:', userData);
      
      if (!userData || !userData.user_id) {
        throw new Error('Please login to make a booking');
      }

      // Build full address
      const fullAddress = formData.postalCode 
        ? `${formData.address}, ${formData.city}, ${formData.postalCode}`
        : `${formData.address}, ${formData.city}`;

      // Prepare data for backend
      const bookingData = {
        artwork_id: artwork.artwork_id,
        customer_name: formData.fullName,
        email: formData.email,
        phone: formData.phone,
        preferred_date: formData.bookingDate,
        preferred_time: formData.bookingTime,
        delivery_address: fullAddress,
        special_instructions: '', // Empty since we removed it from form
        total_amount: artwork.price
      };

      console.log('üì¶ Sending booking data:', bookingData);

      // Call backend API
      const response = await bookingAPI.createBooking(bookingData);
      
      console.log('‚úÖ Backend response:', response.data);

      if (response.data.success) {
        setSuccess(true);
        
        setTimeout(() => {
          console.log('üîÄ Redirecting to my-bookings...');
          navigate('/my-bookings');
        }, 2000);
      } else {
        throw new Error(response.data.message || 'Booking failed');
      }

    } catch (err) {
      console.error('‚ùå Booking error:', err);
      const errorMessage = err.response?.data?.message || err.message || 'Failed to submit booking';
      setError(errorMessage);
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  if (!artwork) {
    return (
      <div className="error-container">
        <div className="error-icon">‚ö†Ô∏è</div>
        <h2>No Artwork Selected</h2>
        <p>Please select an artwork before booking.</p>
        <button className="home-btn" onClick={() => navigate('/')}>
          Go Back Home
        </button>
      </div>
    );
  }

  if (success) {
    return (
      <div className="success-container">
        <div className="success-animation">
          <div className="success-icon">‚úì</div>
          <div className="success-circle"></div>
        </div>
        <h2>Booking Confirmed!</h2>
        <p className="success-message">
          Your consultation with <strong>{artwork.artist_name}</strong> has been booked successfully.
        </p>
        <div className="success-details">
          <p>üìÖ Date: <strong>{formData.bookingDate}</strong></p>
          <p>üïê Time: <strong>{formData.bookingTime}</strong></p>
        </div>
        <p className="success-note">
          The artist will contact you at <strong>{formData.email}</strong> to confirm the details.
        </p>
        <div className="redirect-progress">
          <div className="progress-bar"></div>
        </div>
        <p className="redirect-text">Redirecting to My Bookings...</p>
      </div>
    );
  }

  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="booking-container">
      <button className="back-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <div className="booking-content">
        {/* Left Side - Booking Summary */}
        <div className="booking-summary">
          <h2>Booking Summary</h2>
          
          <div className="summary-artwork">
            <img 
              src={getImageURL(artwork.image_url)} 
              alt={artwork.title}
              className="summary-image"
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/150x180?text=No+Image';
              }}
            />
            <div className="summary-info">
              <h3>{artwork.title}</h3>
              <p className="summary-artist">By {artwork.artist_name}</p>
              <p className="summary-price">NPR {Number(artwork.price).toLocaleString()}</p>
              <div className="summary-badge">{artwork.category}</div>
            </div>
          </div>

          <div className="summary-divider"></div>

          <div className="summary-details">
            <h4>Appointment Details</h4>
            {formData.bookingDate && (
              <div className="summary-item">
                <span className="summary-icon">üìÖ</span>
                <div>
                  <span className="summary-label">Date</span>
                  <span className="summary-value">{formData.bookingDate}</span>
                </div>
              </div>
            )}
            {formData.bookingTime && (
              <div className="summary-item">
                <span className="summary-icon">üïê</span>
                <div>
                  <span className="summary-label">Time</span>
                  <span className="summary-value">{formData.bookingTime}</span>
                </div>
              </div>
            )}
            <div className="summary-item">
              <span className="summary-icon">üìè</span>
              <div>
                <span className="summary-label">Wall Size</span>
                <span className="summary-value">{artwork.wall_size}</span>
              </div>
            </div>
          </div>

          <div className="summary-divider"></div>

          <div className="artist-contact-info">
            <h4>Artist Contact</h4>
            <div className="contact-detail">
              <span className="contact-icon">üìß</span>
              <span>{artwork.contact_email || 'N/A'}</span>
            </div>
            {artwork.phone && (
              <div className="contact-detail">
                <span className="contact-icon">üì±</span>
                <span>{artwork.phone}</span>
              </div>
            )}
          </div>

          <div className="booking-info-box">
            <p>üí° The artist will reach out to you within 24-48 hours to confirm the consultation.</p>
          </div>
        </div>

        {/* Right Side - Booking Form */}
        <div className="booking-form-section">
          <div className="form-header">
            <h2>Your Details</h2>
            <p className="form-subtitle">Please provide your contact information for the consultation</p>
          </div>

          {error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              <span>{error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="booking-form">
            {/* Personal Information */}
            <div className="form-section">
              <h3 className="section-title">Personal Information</h3>
              
              <div className="form-group">
                <label htmlFor="fullName">
                  Full Name <span className="required">*</span>
                </label>
                <input
                  type="text"
                  id="fullName"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="email">
                    Email Address <span className="required">*</span>
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="your.email@example.com"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="phone">
                    Phone Number <span className="required">*</span>
                  </label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="+977-9841234567"
                    required
                  />
                </div>
              </div>
            </div>

            {/* Delivery Address */}
            <div className="form-section">
              <h3 className="section-title">Delivery Address</h3>
              
              <div className="form-group">
                <label htmlFor="address">
                  Street Address <span className="required">*</span>
                </label>
                <input
                  type="text"
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  placeholder="Enter your street address"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="city">
                    City <span className="required">*</span>
                  </label>
                  <input
                    type="text"
                    id="city"
                    name="city"
                    value={formData.city}
                    onChange={handleChange}
                    placeholder="Your city"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="postalCode">
                    Postal Code <span className="optional">(optional)</span>
                  </label>
                  <input
                    type="text"
                    id="postalCode"
                    name="postalCode"
                    value={formData.postalCode}
                    onChange={handleChange}
                    placeholder="Postal code"
                  />
                </div>
              </div>
            </div>

            {/* Appointment Schedule */}
            <div className="form-section">
              <h3 className="section-title">Appointment Schedule</h3>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="bookingDate">
                    Preferred Date <span className="required">*</span>
                  </label>
                  <input
                    type="date"
                    id="bookingDate"
                    name="bookingDate"
                    value={formData.bookingDate}
                    onChange={handleChange}
                    min={today}
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="bookingTime">
                    Preferred Time <span className="required">*</span>
                  </label>
                  <input
                    type="time"
                    id="bookingTime"
                    name="bookingTime"
                    value={formData.bookingTime}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>

            {/* Form Actions */}
            <div className="form-actions">
              <button 
                type="button" 
                className="cancel-btn"
                onClick={() => navigate(-1)}
                disabled={submitting}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="submit-booking-btn"
                disabled={submitting}
              >
                {submitting ? (
                  <>
                    <span className="btn-spinner"></span>
                    <span>Processing...</span>
                  </>
                ) : (
                  <>
                    <span>Confirm Booking</span>
                    <span className="btn-icon">‚Üí</span>
                  </>
                )}
              </button>
            </div>
          </form>

          <div className="booking-notice">
            <div className="notice-icon">‚ÑπÔ∏è</div>
            <div className="notice-content">
              <p><strong>Important:</strong> By confirming this booking, you agree that:</p>
              <ul>
                <li>The artist will contact you to confirm availability</li>
                <li>This is a consultation booking, not a purchase confirmation</li>
                <li>You can discuss pricing and customization during the consultation</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Booking;
