import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { bookingAPI, getImageURL } from '../../services/api';
import './MyBookings.css';

function MyBookings({ user }) {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!user) {
      setError('Please login to view your bookings');
      setLoading(false);
      return;
    }
    fetchBookings();
  }, [user]);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const response = await bookingAPI.getUserBookings();
      
      console.log('Bookings response:', response.data);
      
      if (response.data.success) {
        setBookings(response.data.bookings || []);
      } else {
        setError(response.data.message || 'Failed to fetch bookings');
        setBookings([]);
      }
    } catch (err) {
      setError('Error fetching bookings. Please try again.');
      console.error('Booking fetch error:', err);
      setBookings([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'pending': return 'status-pending';
      case 'confirmed': return 'status-confirmed';
      case 'completed': return 'status-completed';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-default';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (timeString) => {
    return timeString.slice(0, 5);
  };

  const handleBookingClick = (booking) => {
    setSelectedBooking(booking);
  };

  const handleBackToList = () => {
    setSelectedBooking(null);
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading your bookings...</p>
        </div>
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="my-bookings-container">
        <div className="error-container">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h2>Access Denied</h2>
          <p>{error}</p>
          <button onClick={() => navigate('/login')} className="btn-primary">
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  // Detail View
  if (selectedBooking) {
    return (
      <div className="my-bookings-container">
        <div className="bookings-wrapper">
          <button onClick={handleBackToList} className="btn-back-to-list">
            ‚Üê Back to My Bookings
          </button>

          <div className="booking-detail-card">
            <div className="booking-detail-inner">
              <div className="booking-detail-image">
                <img 
                  src={getImageURL(selectedBooking.image_url)} 
                  alt={selectedBooking.artwork_title}
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/400x500?text=Image+Not+Found';
                  }}
                />
              </div>

              <div className="booking-detail-content">
                <div className="booking-header-row">
                  <div>
                    <h1 className="booking-detail-title">{selectedBooking.artwork_title}</h1>
                    <p className="booking-id">Booking ID: #{selectedBooking.id}</p>
                  </div>
                  <span className={`booking-status ${getStatusColor(selectedBooking.status)}`}>
                    {selectedBooking.status.charAt(0).toUpperCase() + selectedBooking.status.slice(1)}
                  </span>
                </div>

                <div className="booking-info-sections">
                  <div className="info-section">
                    <h3>üë§ Customer Details</h3>
                    <p><strong>Name:</strong> {selectedBooking.customer_name}</p>
                    <p><strong>Email:</strong> {selectedBooking.email}</p>
                    <p><strong>Phone:</strong> {selectedBooking.phone}</p>
                  </div>

                  <div className="info-section">
                    <h3>üöö Delivery Details</h3>
                    <p><strong>Date:</strong> {formatDate(selectedBooking.preferred_date)}</p>
                    <p><strong>Time:</strong> {formatTime(selectedBooking.preferred_time)}</p>
                    <p><strong>Address:</strong> {selectedBooking.delivery_address}</p>
                  </div>
                </div>

                {selectedBooking.special_instructions && (
                  <div className="special-instructions">
                    <h3>üìù Special Instructions:</h3>
                    <p>{selectedBooking.special_instructions}</p>
                  </div>
                )}

                <div className="booking-footer">
                  <div className="booking-date">
                    <p>Booked on {formatDate(selectedBooking.created_at)}</p>
                  </div>
                  <div className="booking-amount">
                    <span className="amount-label">Total Amount</span>
                    <span className="amount-value">NPR {selectedBooking.total_amount.toLocaleString()}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // List View
  return (
    <div className="my-bookings-container">
      <div className="bookings-wrapper">
        <div className="bookings-header">
          <div className="header-content">
            <div className="header-text">
              <h1>My Bookings</h1>
              <p>Welcome back, {user?.username || user?.full_name || 'User'}!</p>
            </div>
            <div className="header-actions">
              <div className="booking-count">
                <span className="count-label">Total Bookings</span>
                <span className="count-number">{bookings.length}</span>
              </div>
              <button onClick={() => navigate('/')} className="btn-back">
                ‚Üê Back to Home
              </button>
            </div>
          </div>
        </div>

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">üì¶</div>
            <h3>No Bookings Yet</h3>
            <p>You haven't made any bookings. Start exploring our artworks!</p>
            <button onClick={() => navigate('/')} className="btn-primary">
              Browse Artworks
            </button>
          </div>
        ) : (
          <div className="bookings-list-view">
            {bookings.map((booking) => (
              <div 
                key={booking.id} 
                className="booking-list-item"
                onClick={() => handleBookingClick(booking)}
              >
                <div className="booking-list-image">
                  <img 
                    src={getImageURL(booking.image_url)} 
                    alt={booking.artwork_title}
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found';
                    }}
                  />
                </div>
                <div className="booking-list-info">
                  <h3 className="booking-list-title">{booking.artwork_title}</h3>
                  <p className="booking-list-id">Booking #{booking.id}</p>
                  <div className="booking-list-meta">
                    <span className={`booking-list-status ${getStatusColor(booking.status)}`}>
                      {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                    </span>
                    <span className="booking-list-amount">NPR {booking.total_amount.toLocaleString()}</span>
                  </div>
                  <p className="booking-list-date">{formatDate(booking.created_at)}</p>
                </div>
                <div className="booking-list-arrow">‚Üí</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MyBookings;
