// frontend/src/components/Footer/Footer.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // ‚≠ê REMOVED useNavigate
import { artworkAPI, artistAPI } from '../../services/api';
import './Footer.css';

function Footer({ isAuthenticated, user }) {
  // ‚≠ê REMOVED: const navigate = useNavigate();
  const [stats, setStats] = useState({ artworks: 0, artists: 0 });
  const [email, setEmail] = useState('');
  const [subscribed, setSubscribed] = useState(false);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [artworksRes, artistsRes] = await Promise.all([
        artworkAPI.getAllArtworks(),
        artistAPI.getAllArtists()
      ]);
      
      setStats({
        artworks: artworksRes.data.data?.length || 0,
        artists: artistsRes.data.data?.length || 0
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleSubscribe = (e) => {
    e.preventDefault();
    if (email) {
      setSubscribed(true);
      setEmail('');
      setTimeout(() => setSubscribed(false), 3000);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const currentYear = new Date().getFullYear();

  return (
    <footer className="footer">
      <div className="footer-content">
        <div className="footer-card">
          {/* Logo and Tagline */}
          <div className="footer-brand">
            <h2 className="footer-logo">üé® WALL ART GO</h2>
            <p className="footer-tagline">"Transform Your Space With Art"</p>
            
            {/* Quick Stats */}
            <div className="footer-stats">
              <div className="stat-item">
                <span className="stat-number">{stats.artworks}+</span>
                <span className="stat-label">Artworks</span>
              </div>
              <div className="stat-divider"></div>
              <div className="stat-item">
                <span className="stat-number">{stats.artists}+</span>
                <span className="stat-label">Artists</span>
              </div>
            </div>
          </div>

          {/* Three Columns */}
          <div className="footer-columns">
            {/* Column 1: Explore */}
            <div className="footer-column">
              <h3 className="column-title">EXPLORE</h3>
              <ul className="footer-links">
                <li><Link to="/">Browse Art</Link></li>
                <li><Link to="/">Artists</Link></li>
                <li><Link to="/">Best Sellers</Link></li>
                {isAuthenticated && (
                  <>
                    {user?.role === 'admin' ? (
                      <li><Link to="/admin/bookings">Admin Dashboard</Link></li>
                    ) : (
                      <li><Link to="/my-bookings">My Bookings</Link></li>
                    )}
                  </>
                )}
                {!isAuthenticated && (
                  <li><Link to="/login">Login</Link></li>
                )}
              </ul>
            </div>

            {/* Column 2: Support */}
            <div className="footer-column">
              <h3 className="column-title">SUPPORT</h3>
              <ul className="footer-links">
                <li><a href="mailto:contact@wallartgo.com">Contact Us</a></li>
                <li><a href="#faq">FAQ</a></li>
                <li><a href="#help">Help Center</a></li>
                <li><a href="#shipping">Shipping Info</a></li>
              </ul>
            </div>

            {/* Column 3: Newsletter */}
            <div className="footer-column">
              <h3 className="column-title">STAY UPDATED</h3>
              <p className="newsletter-text">Subscribe to get updates on new artworks</p>
              
              <form onSubmit={handleSubscribe} className="newsletter-form">
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  className="newsletter-input"
                />
                <button type="submit" className="newsletter-btn">
