import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { artworkAPI, bookingAPI, getImageURL } from '../../services/api';
import './Booking.css';

function Booking({ user }) {
  const { artworkId } = useParams();
  const navigate = useNavigate();
  const [artwork, setArtwork] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    bookingDate: '',
    bookingTime: '',
    address: '',
    city: '',
    notes: ''
  });

  useEffect(() => {
    const fetchArtwork = async () => {
      try {
        setLoading(true);
        const response = await artworkAPI.getArtworkById(artworkId);
        setArtwork(response.data.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching artwork:', err);
        setError('Failed to load artwork details.');
        setLoading(false);
      }
    };
    if (artworkId) fetchArtwork();
  }, [artworkId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      // 1. Get User ID from props (passed from App.js) or localStorage fallback
      const userId = user?.user_id || user?.id || JSON.parse(localStorage.getItem('user'))?.id;

      if (!userId) {
        throw new Error("User session not found. Please log in again.");
      }

      // 2. Map data to match PostgreSQL columns exactly
      const bookingData = {
        user_id: userId,
        artwork_id: parseInt(artwork.artwork_id),
        customer_name: formData.fullName,
        email: formData.email,
        phone: formData.phone,
        preferred_date: formData.bookingDate,
        preferred_time: formData.bookingTime,
        delivery_address: `${formData.address}, ${formData.city}`.trim(),
        special_instructions: formData.notes,
        total_amount: parseFloat(artwork.price)
      };

      console.log('Sending to wallarts_pro:', bookingData);

      const response = await bookingAPI.createBooking(bookingData);
      
      if (response.data.success) {
        setSuccess(true);
        setTimeout(() => navigate('/my-bookings'), 2000);
      } else {
        throw new Error(response.data.message || 'Failed to create booking');
      }
    } catch (err) {
      console.error('❌ Booking error:', err);
      setError(err.response?.data?.message || err.message || 'An error occurred');
      setSubmitting(false);
    }
  };

  if (loading) return <div className="loading-container">Loading artwork...</div>;
  if (!artwork) return <div className="error-container">Artwork not found.</div>;

  return (
    <div className="booking-container">
      <div className="booking-header">
        {/* Use getImageURL here to remove the yellow warning on the import line */}
        <img 
          src={getImageURL(artwork.image_url)} 
          alt={artwork.title} 
          style={{ width: '100px', borderRadius: '8px' }} 
        />
        <h2>Confirm Booking for "{artwork.title}"</h2>
      </div>

      {success ? (
        <div className="success-msg">Booking Successful! Redirecting...</div>
      ) : (
        <form onSubmit={handleSubmit} className="booking-form">
          {error && <div className="error-banner">{error}</div>}
          
          <div className="form-group">
            <label>Full Name</label>
            <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} required />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Email</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Preferred Date</label>
              <input type="date" name="bookingDate" value={formData.bookingDate} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Preferred Time</label>
              <input type="time" name="bookingTime" value={formData.bookingTime} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-group">
            <label>Address</label>
            <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="Street address" />
          </div>

          <div className="form-group">
            <label>City</label>
            <input type="text" name="city" value={formData.city} onChange={handleChange} />
          </div>

          <div className="form-group">
            <label>Special Instructions</label>
            <textarea name="notes" value={formData.notes} onChange={handleChange} rows="3"></textarea>
          </div>

          <button type="submit" disabled={submitting} className="submit-btn">
            {submitting ? 'Processing...' : `Confirm Booking - NPR ${Number(artwork.price).toLocaleString()}`}
          </button>
        </form>
      )}
    </div>
  );
}

export default Booking;
