import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Artwork APIs
export const artworkAPI = {
  // Get all artworks with optional sorting
  getAllArtworks: (sortBy) => {
    const params = sortBy ? { sort: sortBy } : {};
    return api.get('/artworks', { params });
  },
  
  // Get single artwork by ID
  getArtworkById: (id) => {
    return api.get(`/artworks/${id}`);
  },
  
  // Get artworks by artist
  getArtworksByArtist: (artistId) => {
    return api.get(`/artworks/artist/${artistId}`);
  },
  
  // Search artworks
  searchArtworks: (searchTerm) => {
    return api.get('/artworks/search', { params: { q: searchTerm } });
  }
};

// Artist APIs
export const artistAPI = {
  // Get all artists
  getAllArtists: () => {
    return api.get('/artists');
  },
  
  // Get artist with artworks
  getArtistById: (id) => {
    return api.get(`/artists/${id}`);
  }
};

// Helper function to get image URL
export const getImageURL = (imageName) => {
  return `http://localhost:5000/images/${imageName}`;
};

export default api;
