import React, { useState, useEffect } from 'react';

const API_BASE_URL = 'http://localhost:5000/api';
const getImageURL = (imageName) => `http://localhost:5000/images/${imageName}`;

const UserDashboard = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
      fetchBookings(JSON.parse(userData).token);
    } else {
      setError('Please login to view your bookings');
      setLoading(false);
    }
  }, []);

  const fetchBookings = async (token) => {
    try {
      const response = await fetch(`${API_BASE_URL}/bookings/my-bookings`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();
      
      if (data.success) {
        setBookings(data.data);
      } else {
        setError(data.message || 'Failed to fetch bookings');
      }
    } catch (err) {
      setError('Error fetching bookings');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'confirmed': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (timeString) => {
    return timeString.slice(0, 5); // HH:MM format
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-4 border-blue-500 border-t-transparent"></div>
          <p className="mt-4 text-gray-600">Loading your bookings...</p>
        </div>
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full text-center">
          <div className="text-red-500 text-5xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Access Denied</h2>
          <p className="text-gray-600 mb-6">{error}</p>
          <a href="/login" className="inline-block bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors">
            Go to Login
          </a>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-2xl shadow-lg p-6 mb-8">
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div>
              <h1 className="text-3xl font-bold text-gray-800 mb-2">My Bookings</h1>
              <p className="text-gray-600">Welcome back, {user?.username || user?.full_name || 'User'}!</p>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-right">
                <p className="text-sm text-gray-500">Total Bookings</p>
                <p className="text-2xl font-bold text-blue-600">{bookings.length}</p>
              </div>
              <a href="/" className="bg-gray-100 hover:bg-gray-200 text-gray-800 px-4 py-2 rounded-lg font-semibold transition-colors">
                ‚Üê Back to Home
              </a>
            </div>
          </div>
        </div>

        {/* Bookings List */}
        {bookings.length === 0 ? (
          <div className="bg-white rounded-2xl shadow-lg p-12 text-center">
            <div className="text-gray-300 text-6xl mb-4">üì¶</div>
            <h3 className="text-2xl font-bold text-gray-800 mb-2">No Bookings Yet</h3>
            <p className="text-gray-600 mb-6">You haven't made any bookings. Start exploring our artworks!</p>
            <a href="/" className="inline-block bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors">
              Browse Artworks
            </a>
          </div>
        ) : (
          <div className="space-y-6">
            {bookings.map((booking) => (
              <div key={booking.booking_id} className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow">
                <div className="md:flex">
                  {/* Artwork Image */}
                  <div className="md:w-64 md:flex-shrink-0">
                    <img 
                      src={getImageURL(booking.image_url)} 
                      alt={booking.artwork_title}
                      className="h-full w-full object-cover"
                    />
                  </div>

                  {/* Booking Details */}
                  <div className="p-6 flex-1">
                    <div className="flex items-start justify-between mb-4">
                      <div>
                        <h3 className="text-2xl font-bold text-gray-800 mb-1">
                          {booking.artwork_title}
                        </h3>
                        <p className="text-sm text-gray-500">
                          Booking ID: #{booking.booking_id}
                        </p>
                      </div>
                      <span className={`px-4 py-2 rounded-full text-sm font-semibold ${getStatusColor(booking.status)}`}>
                        {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                      </span>
                    </div>

                    <div className="grid md:grid-cols-2 gap-4 mb-4">
                      {/* Customer Info */}
                      <div>
                        <h4 className="font-semibold text-gray-700 mb-2 flex items-center gap-2">
                          <span>üë§</span> Customer Details
                        </h4>
                        <p className="text-gray-600 text-sm mb-1">
                          <span className="font-medium">Name:</span> {booking.customer_name}
                        </p>
                        <p className="text-gray-600 text-sm mb-1">
                          <span className="font-medium">Email:</span> {booking.email}
                        </p>
                        <p className="text-gray-600 text-sm">
                          <span className="font-medium">Phone:</span> {booking.phone}
                        </p>
                      </div>

                      {/* Delivery Info */}
                      <div>
                        <h4 className="font-semibold text-gray-700 mb-2 flex items-center gap-2">
                          <span>üöö</span> Delivery Details
                        </h4>
                        <p className="text-gray-600 text-sm mb-1">
                          <span className="font-medium">Date:</span> {formatDate(booking.preferred_date)}
                        </p>
                        <p className="text-gray-600 text-sm mb-1">
                          <span className="font-medium">Time:</span> {formatTime(booking.preferred_time)}
                        </p>
                        <p className="text-gray-600 text-sm">
                          <span className="font-medium">Address:</span> {booking.delivery_address}
                        </p>
                      </div>
                    </div>

                    {/* Special Instructions */}
                    {booking.special_instructions && (
                      <div className="mb-4 bg-gray-50 rounded-lg p-3">
                        <h4 className="font-semibold text-gray-700 mb-1 text-sm">üìù Special Instructions:</h4>
                        <p className="text-gray-600 text-sm">{booking.special_instructions}</p>
                      </div>
                    )}

                    {/* Footer */}
                    <div className="flex items-center justify-between pt-4 border-t border-gray-200">
                      <div>
                        <p className="text-sm text-gray-500">Booked on {formatDate(booking.created_at)}</p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500">Total Amount</p>
                        <p className="text-2xl font-bold text-green-600">
                          ‚Çπ{booking.total_amount.toLocaleString()}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
