// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom'; // ‚≠ê ADD useLocation

import Navbar from './components/Navbar/Navbar';
import Home from './components/Home/Home';
import ArtworkDetail from './components/ArtworkDetail/ArtworkDetail';
import ArtistProfile from './components/ArtistProfile/ArtistProfile';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';
import Booking from './components/Booking/Booking';
import MyBookings from './components/MyBookings/MyBookings';
import AdminBookings from './components/AdminBookings/AdminBookings';
import ScrollToTop from './components/shared/ScrollToTop';
import SplashScreen from './components/SplashScreen/SplashScreen';
import Footer from './components/Footer/Footer';

import './App.css';

function App() {
  // ‚≠ê NEW STATE - Splash screen
  const [showSplash, setShowSplash] = useState(true);

  // Authentication state
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);

  // Check if user is logged in on component mount
  useEffect(() => {
    const checkAuth = () => {
      console.log('üîç Checking authentication status...');
      
      // ‚≠ê NEW LINE - Clear localStorage to prevent auto-login
      localStorage.removeItem('user');
      
      const loggedInUser = localStorage.getItem('user');
      
      if (loggedInUser) {
        try {
          const userData = JSON.parse(loggedInUser);
          
          console.log('üë§ Found user in localStorage:', userData);
          
          // Validate that the user object has required fields
          if (userData.user_id && userData.token) {
            setUser(userData);
            setIsAuthenticated(true);
            console.log('‚úÖ User authenticated:', userData.email || userData.username);
          } else {
            console.warn('‚ö†Ô∏è Invalid user data in localStorage, clearing...');
            localStorage.removeItem('user');
          }
        } catch (error) {
          console.error('‚ùå Error parsing user data:', error);
          localStorage.removeItem('user');
        }
      } else {
        console.log('‚ÑπÔ∏è No user found in localStorage');
      }
      
      setLoadingUser(false);
    };
    
    checkAuth();
  }, []);

  // Handle logout
  const handleLogout = () => {
    console.log('üëã Logging out user...');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  // ‚≠ê NEW FUNCTION - Handle splash finish
  const handleSplashFinish = () => {
    setShowSplash(false);
  };

  // Protected Route Component
  const ProtectedRoute = ({ children }) => {
    const location = useLocation(); // ‚≠ê ADD THIS LINE
    
    if (loadingUser) {
      return (
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh' 
        }}>
          Loading...
        </div>
      );
    }
    
    if (!isAuthenticated) {
      console.log('üö´ Access denied - redirecting to login');
      return <Navigate to="/login" state={{ from: location.pathname }} replace />; // ‚≠ê MODIFIED THIS LINE
    }
    
    return children;
  };

  // ‚≠ê NEW - Show splash screen first
  if (showSplash) {
    return <SplashScreen onFinish={handleSplashFinish} />;
  }

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="App">
        <ScrollToTop />

        <Navbar
          isAuthenticated={isAuthenticated}
          user={user}
          onLogout={handleLogout}
        />

        <Routes>
          {/* Public Routes */}
          <Route
            path="/login"
            element={
              isAuthenticated ? (
                <Navigate to="/" replace />
              ) : (
                <Login
                  setIsAuthenticated={setIsAuthenticated}
                  setUser={setUser}
                />
              )
            }
          />
          
          <Route
            path="/signup"
            element={
              isAuthenticated ? (
                <Navigate to="/" replace />
              ) : (
                <Signup
                  setIsAuthenticated={setIsAuthenticated}
                  setUser={setUser}
                />
              )
            }
          />
          
          <Route path="/" element={<Home />} />
          <Route path="/artwork/:id" element={<ArtworkDetail />} />
          <Route path="/artist/:id" element={<ArtistProfile />} />

          {/* Protected Routes */}
          <Route
            path="/booking/:artworkId"
            element={
              <ProtectedRoute>
                <Booking user={user} />
              </ProtectedRoute>
            }
          />

          <Route
            path="/my-bookings"
            element={
              <ProtectedRoute>
                <MyBookings user={user} />
              </ProtectedRoute>
            }
          />

          {/* ‚≠ê NEW ADMIN ROUTE */}
          <Route
            path="/admin/bookings"
            element={
              <ProtectedRoute>
                <AdminBookings user={user} />
              </ProtectedRoute>
            }
          />

          {/* Fallback */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>

        {/* ‚≠ê NEW - Footer on all pages */}
        <Footer isAuthenticated={isAuthenticated} user={user} />
      </div>
    </Router>
  );
}

export default App;
