import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { artworkAPI, artistAPI, getImageURL } from '../../services/api';
import './Home.css';

function Home() {
  const navigate = useNavigate();
  const [artists, setArtists] = useState([]);
  const [artworksByArtist, setArtworksByArtist] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeSort, setActiveSort] = useState('');
  const [showPriceDropdown, setShowPriceDropdown] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // 1. Fetch Artists (Backend returns array directly or inside .data)
      const artistsResponse = await artistAPI.getAllArtists();
      const artistsData = Array.isArray(artistsResponse.data) ? artistsResponse.data : (artistsResponse.data.data || []);
      setArtists(artistsData);
      
      // 2. Fetch Artworks using the new 'id' field
      const artworksPromises = artistsData.map(artist =>
        artworkAPI.getArtworksByArtist(artist.id) 
      );
      
      const artworksResponses = await Promise.all(artworksPromises);
      
      const artworksMap = {};
      artistsData.forEach((artist, index) => {
        // Handle response structure safely
        const artistArtworks = Array.isArray(artworksResponses[index].data) ? artworksResponses[index].data : (artworksResponses[index].data.data || []);
        
        // Filter using 'id' (matches new database)
        artworksMap[artist.id] = artistArtworks.filter(
          art => art.id !== 20
        );
      });
      
      setArtworksByArtist(artworksMap);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleSort = async (sortType) => {
    try {
      setActiveSort(sortType);
      setShowPriceDropdown(false);
      
      const response = await artworkAPI.getAllArtworks(sortType);
      const sortedArtworks = Array.isArray(response.data) ? response.data : (response.data.data || []);
      
      const artworksMap = {};
      artists.forEach(artist => {
        artworksMap[artist.id] = sortedArtworks.filter(
          art => art.artist_id === artist.id && art.id !== 20
        );
      });
      
      setArtworksByArtist(artworksMap);
    } catch (error) {
      console.error('Error sorting artworks:', error);
    }
  };

  const scrollSlider = (artistId, direction) => {
    const slider = document.getElementById(`slider-${artistId}`);
    if (slider) {
      slider.scrollBy({ left: direction * 380, behavior: 'smooth' });
    }
  };

  const viewArtworkDetails = (artworkId) => {
    navigate(`/artwork/${artworkId}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading artworks...</p>
      </div>
    );
  }

  return (
    <div className="home-container">
      <div className="header">
        <h1>WALL ART GO</h1>
        <p>Curated Collection of Exceptional Artworks</p>
      </div>

      <div className="top-bar">
        <div className="sort-options">
          <span className="sort-label">Sort by:</span>
          
          <div className="dropdown">
            <button 
              className={`sort-btn ${activeSort.includes('price') ? 'active' : ''}`}
              onClick={() => setShowPriceDropdown(!showPriceDropdown)}
            >
              {activeSort === 'price_low' ? 'Price: Low to High' : 
               activeSort === 'price_high' ? 'Price: High to Low' : 
               'Price'} ▼
            </button>
            {showPriceDropdown && (
              <div className="dropdown-content">
                <button onClick={() => handleSort('price_low')}>Low to High</button>
                <button onClick={() => handleSort('price_high')}>High to Low</button>
              </div>
            )}
          </div>
          
          <button 
            className={`sort-btn ${activeSort === 'bestseller' ? 'active' : ''}`}
            onClick={() => handleSort('bestseller')}
          >
            Best Sellers
          </button>
          
          <button 
            className={`sort-btn ${activeSort === 'newest' ? 'active' : ''}`}
            onClick={() => handleSort('newest')}
          >
            Newest
          </button>
        </div>
      </div>

      <div className="artists-container">
        {artists.map((artist) => (
          <div key={artist.id} className="artist-section">
            <div className="artist-header">
              <div className="artist-info">
                {/* Wired to 'name' from DB */}
                <h2>{artist.name}</h2> 
                <p className="artist-bio">{artist.bio || 'Professional Artist'}</p>
              </div>
              <button 
                className="view-profile-btn"
                onClick={() => navigate(`/artist/${artist.id}`)}
              >
                View Profile →
              </button>
            </div>

            <div className="slider-container">
              <button 
                className="slider-arrow left"
                onClick={() => scrollSlider(artist.id, -1)}
              >
                ‹
              </button>

              <div className="artworks-slider" id={`slider-${artist.id}`}>
                {(artworksByArtist[artist.id] || []).map((artwork) => (
                  <div 
                    key={artwork.id} 
                    className="artwork-card"
                    onClick={() => viewArtworkDetails(artwork.id)}
                  >
                    <div className="artwork-image-container">
                      <img 
                        src={getImageURL(artwork.image_url)} 
                        alt={artwork.title}
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/300x400?text=No+Image';
                        }}
                      />
                      {artwork.is_bestseller && (
                        <span className="bestseller-badge">⭐ Best Seller</span>
                      )}
                    </div>
                    <div className="artwork-info">
                      <h3>{artwork.title}</h3>
                      <p className="artwork-size">{artwork.wall_size}</p>
                      <p className="artwork-price">NPR {Number(artwork.price).toLocaleString()}</p>
                      <button className="view-details-btn">View Details</button>
                    </div>
                  </div>
                ))}
                
                {(!artworksByArtist[artist.id] || artworksByArtist[artist.id].length === 0) && (
                   <p style={{padding: '20px', color: '#666'}}>No artworks found for this artist.</p>
                )}
              </div>

              <button 
                className="slider-arrow right"
                onClick={() => scrollSlider(artist.id, 1)}
              >
                ›
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
