import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add token to ALL requests automatically
api.interceptors.request.use(
  (config) => {
    const user = localStorage.getItem('user');
    
    if (user) {
      try {
        const userData = JSON.parse(user);
        
        // Check multiple possible locations for the token
        let token = null;
        
        // Try different possible token locations
        if (userData.token) {
          token = userData.token;
        } else if (userData.data && userData.data.token) {
          token = userData.data.token;
        } else if (userData.user && userData.user.token) {
          token = userData.user.token;
        }
        
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
          console.log('Token added to request:', token.substring(0, 20) + '...');
        } else {
          console.warn('No token found in user data:', userData);
        }
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    } else {
      console.warn('No user data in localStorage');
    }
    
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Artwork APIs
export const artworkAPI = {
  getAllArtworks: (sortBy) => {
    const params = sortBy ? { sort: sortBy } : {};
    return api.get('/artworks', { params });
  },
  getArtworkById: (id) => api.get(`/artworks/${id}`),
  getArtworksByArtist: (artistId) => api.get(`/artworks/artist/${artistId}`),
  searchArtworks: (searchTerm) => api.get('/artworks/search', { params: { q: searchTerm } })
};

// Artist APIs
export const artistAPI = {
  getAllArtists: () => api.get('/artists'),
  getArtistById: (id) => api.get(`/artists/${id}`)
};

// Auth APIs
export const authAPI = {
  register: (data) => api.post('/auth/register', data),
  login: (data) => api.post('/auth/login', data)
};

// Booking APIs
export const bookingAPI = {
  createBooking: (data) => api.post('/bookings', data),
  getUserBookings: () => api.get('/bookings/my-bookings'),
  getBookingById: (id) => api.get(`/bookings/${id}`)
};

// Helper function to get image URL
export const getImageURL = (imageName) => {
  return `http://localhost:5000/images/${imageName}`;
};

export default api;
