import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { bookingAPI, getImageURL } from '../../services/api';
import './MyBookings.css';

function MyBookings() {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      console.log('Loading bookings from backend...');
      
      const response = await bookingAPI.getUserBookings();
      console.log('Bookings response:', response.data);
      
      setBookings(response.data.data || []);
      setLoading(false);
    } catch (err) {
      console.error('Error loading bookings:', err);
      setError(err.response?.data?.message || 'Failed to load bookings');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  const formatTime = (timeString) => {
    if (!timeString) return 'N/A';
    return timeString;
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      pending: 'status-pending',
      confirmed: 'status-confirmed',
      completed: 'status-completed',
      cancelled: 'status-cancelled'
    };
    return statusClasses[status] || 'status-pending';
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading your bookings...</p>
      </div>
    );
  }

  return (
    <div className="my-bookings-container">
      <div className="bookings-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê Back to Home
        </button>
        <h1>My Bookings</h1>
        <p className="bookings-subtitle">View your booking history</p>
      </div>

      {error && (
        <div className="error-message">
          <span className="error-icon">‚ö†Ô∏è</span>
          <span>{error}</span>
        </div>
      )}

      {bookings.length === 0 ? (
        <div className="no-bookings">
          <div className="no-bookings-icon">
            <svg viewBox="0 0 100 100" width="120" height="120">
              <rect x="20" y="30" width="60" height="50" rx="5" fill="#e0e7ff" />
              <rect x="25" y="35" width="15" height="15" rx="2" fill="#818cf8" />
              <rect x="25" y="55" width="15" height="15" rx="2" fill="#818cf8" />
              <rect x="45" y="38" width="30" height="3" rx="1" fill="#818cf8" />
              <rect x="45" y="58" width="30" height="3" rx="1" fill="#818cf8" />
            </svg>
          </div>
          <h2>You haven't made any bookings yet</h2>
          <p>Start exploring our collection and book your favorite artwork!</p>
          <button className="explore-btn" onClick={() => navigate('/')}>
            Explore Artworks
          </button>
        </div>
      ) : (
        <div className="bookings-grid">
          {bookings.map((booking) => (
            <div key={booking.booking_id} className="booking-card">
              <div className="booking-image">
                <img 
                  src={getImageURL(booking.image_url)} 
                  alt={booking.title}
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
                  }}
                />
                <span className={`status-badge ${getStatusBadge(booking.status)}`}>
                  {booking.status}
                </span>
              </div>
              
              <div className="booking-details">
                <h3 className="booking-title">{booking.title}</h3>
                <p className="booking-artist">By {booking.artist_name}</p>
                
                <div className="booking-info">
                  <div className="info-row">
                    <span className="info-icon">üìÖ</span>
                    <span className="info-label">Date:</span>
                    <span className="info-value">{formatDate(booking.preferred_date)}</span>
                  </div>
                  
                  <div className="info-row">
                    <span className="info-icon">üïê</span>
                    <span className="info-label">Time:</span>
                    <span className="info-value">{formatTime(booking.preferred_time)}</span>
                  </div>
                  
                  <div className="info-row">
                    <span className="info-icon">üí∞</span>
                    <span className="info-label">Price:</span>
                    <span className="info-value">NPR {Number(booking.price).toLocaleString()}</span>
                  </div>

                  {booking.delivery_address && (
                    <div className="info-row">
                      <span className="info-icon">üìç</span>
                      <span className="info-label">Address:</span>
                      <span className="info-value">{booking.delivery_address}</span>
                    </div>
                  )}
                </div>

                <div className="booking-customer">
                  <p><strong>Customer:</strong> {booking.customer_name}</p>
                  <p><strong>Email:</strong> {booking.email}</p>
                  <p><strong>Phone:</strong> {booking.phone}</p>
                </div>

                {booking.special_instructions && (
                  <div className="booking-notes">
                    <p><strong>Notes:</strong></p>
                    <p className="notes-text">{booking.special_instructions}</p>
                  </div>
                )}

                <div className="booking-date">
                  <small>Booked on: {formatDate(booking.booking_date)}</small>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyBookings;
