import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { artworkAPI, artistAPI, getImageURL } from '../../services/api';
import './Home.css';

function Home() {
  const navigate = useNavigate();
  const [artists, setArtists] = useState([]);
  const [artworks, setArtworks] = useState([]);
  const [groupedArtworks, setGroupedArtworks] = useState({});
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('newest');
  const [searchTerm, setSearchTerm] = useState('');

  // Memoize groupArtworksByArtist to avoid re-creating it on every render
  const groupArtworksByArtist = useCallback((artworksData) => {
    const grouped = {};
    
    artworksData.forEach(artwork => {
      const artistId = artwork.artist_id;
      if (!grouped[artistId]) {
        const artist = artists.find(a => a.artist_id === artistId);
        grouped[artistId] = {
          artist: artist || { artist_name: artwork.artist_name, artist_id: artistId },
          artworks: []
        };
      }
      grouped[artistId].artworks.push(artwork);
    });

    setGroupedArtworks(grouped);
  }, [artists]);

  // Memoize handleSearch to avoid re-creating it on every render
  const handleSearch = useCallback(async () => {
    if (!searchTerm.trim()) {
      groupArtworksByArtist(artworks);
      return;
    }

    try {
      const response = await artworkAPI.searchArtworks(searchTerm);
      if (response.data.success) {
        groupArtworksByArtist(response.data.data);
      }
    } catch (error) {
      console.error('Search error:', error);
    }
  }, [searchTerm, artworks, groupArtworksByArtist]);

  useEffect(() => {
    fetchData();
  }, [sortBy]); // Only re-fetch when sortBy changes

  useEffect(() => {
    if (searchTerm) {
      handleSearch();
    } else {
      groupArtworksByArtist(artworks);
    }
  }, [searchTerm, artworks, handleSearch, groupArtworksByArtist]);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch artists
      const artistsResponse = await artistAPI.getAllArtists();
      if (artistsResponse.data.success) {
        setArtists(artistsResponse.data.data);
      }

      // Fetch artworks with sorting
      const artworksResponse = await artworkAPI.getAllArtworks(sortBy);
      if (artworksResponse.data.success) {
        const artworksData = artworksResponse.data.data;
        setArtworks(artworksData);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  const handleArtworkClick = (artworkId) => {
    navigate(`/artwork/${artworkId}`);
  };

  const handleArtistClick = (artistId) => {
    navigate(`/artist/${artistId}`);
  };

  if (loading) {
    return (
      <div className="home-container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading artworks...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">Discover Beautiful Wall Art</h1>
          <p className="hero-subtitle">Transform your space with stunning artwork from talented artists</p>
        </div>
      </div>

      {/* Search and Filter Bar */}
      <div className="controls-bar">
        <div className="search-container">
          <input
            type="text"
            className="search-input"
            placeholder="Search artworks, artists, categories..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="search-button" onClick={handleSearch}>
            üîç Search
          </button>
        </div>

        <div className="sort-container">
          <label htmlFor="sort">Sort by:</label>
          <select id="sort" value={sortBy} onChange={handleSortChange} className="sort-select">
            <option value="newest">Newest First</option>
            <option value="bestseller">Bestsellers</option>
            <option value="price_low">Price: Low to High</option>
            <option value="price_high">Price: High to Low</option>
          </select>
        </div>
      </div>

      {/* Artworks Grouped by Artist */}
      <div className="artworks-container">
        {Object.keys(groupedArtworks).length === 0 ? (
          <div className="no-results">
            <h3>No artworks found</h3>
            <p>Try adjusting your search or filters</p>
          </div>
        ) : (
          Object.values(groupedArtworks).map((group) => (
            <div key={group.artist.artist_id} className="artist-section">
              {/* Artist Header */}
              <div className="artist-header">
                <div className="artist-info">
                  <h2 className="artist-name">{group.artist.artist_name}</h2>
                  {group.artist.bio && (
                    <p className="artist-bio">{group.artist.bio.substring(0, 100)}...</p>
                  )}
                </div>
                <button 
                  className="view-artist-btn"
                  onClick={() => handleArtistClick(group.artist.artist_id)}
                >
                  View Artist Profile ‚Üí
                </button>
              </div>

              {/* Artworks Grid */}
              <div className="artworks-grid">
                {group.artworks.map((artwork) => (
                  <div 
                    key={artwork.artwork_id} 
                    className="artwork-card"
                    onClick={() => handleArtworkClick(artwork.artwork_id)}
                  >
                    {/* Artwork Image */}
                    <div className="artwork-image-container">
                      <img 
                        src={getImageURL(artwork.image_url)} 
                        alt={artwork.title}
                        className="artwork-image"
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/300x400?text=No+Image';
                        }}
                      />
                      {artwork.is_bestseller && (
                        <span className="bestseller-badge">‚≠ê Bestseller</span>
                      )}
                    </div>

                    {/* Artwork Info */}
                    <div className="artwork-info">
                      <h3 className="artwork-title">{artwork.title}</h3>
                      <p className="artwork-description">
                        {artwork.description?.substring(0, 80)}...
                      </p>
                      
                      <div className="artwork-details">
                        <span className="artwork-size">üìè {artwork.wall_size}</span>
                        <span className="artwork-category">üé® {artwork.category}</span>
                      </div>

                      <div className="artwork-footer">
                        <span className="artwork-price">‚Çπ{artwork.price.toLocaleString()}</span>
                        <span className="artwork-views">üëÅÔ∏è {artwork.views} views</span>
                      </div>

                      <button className="view-details-btn">
                        View Details
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Stats Section */}
      <div className="stats-section">
        <div className="stat-item">
          <h3>{artists.length}+</h3>
          <p>Talented Artists</p>
        </div>
        <div className="stat-item">
          <h3>{artworks.length}+</h3>
          <p>Beautiful Artworks</p>
        </div>
        <div className="stat-item">
          <h3>100%</h3>
          <p>Customer Satisfaction</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
