// frontend/src/components/Auth/Signup.js

import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { authAPI } from '../../services/api';
import './Auth.css';

const Signup = ({ setIsAuthenticated, setUser }) => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    full_name: '',
    phone: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { username, email, password, confirmPassword, full_name, phone } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setLoading(true);

    try {
      console.log('ðŸ“ Attempting signup...');
      
      // Call the actual Backend API
      const response = await authAPI.register({ 
        username, 
        email, 
        password, 
        full_name, 
        phone 
      });
      
      console.log('âœ… Signup response:', response.data);
      
      if (response.data.success) {
        // Extract user data from the nested 'data' object
        const userData = response.data.data;
        
        console.log('ðŸ‘¤ User data to store:', userData);
        
        // Store the COMPLETE user object with token
        const userToStore = {
          user_id: userData.user_id,
          username: userData.username,
          email: userData.email,
          full_name: userData.full_name,
          role: userData.role,
          token: userData.token  // â­ CRITICAL: Store the token!
        };
        
        console.log('ðŸ’¾ Storing in localStorage:', userToStore);
        localStorage.setItem('user', JSON.stringify(userToStore));
        
        // Update app state
        setUser(userToStore);
        setIsAuthenticated(true);
        
        console.log('âœ… Signup successful, redirecting...');
        
        const from = location.state?.from || '/';
        navigate(from, { replace: true });
      }
    } catch (err) {
      console.error('âŒ Signup error:', err);
      setError(err.response?.data?.message || 'Registration failed. Try a different email.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card signup-card">
        <div className="auth-header">
          <h2>Create Account</h2>
          <p className="auth-subtitle">Join Wall Arts today</p>
        </div>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={onSubmit} className="auth-form">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="username">Username *</label>
              <input
                type="text"
                id="username"
                name="username"
                value={username}
                onChange={onChange}
                required
                minLength="3"
                placeholder="Choose a username"
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email Address *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={onChange}
                required
                placeholder="Enter your email"
                className="form-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="full_name">Full Name</label>
              <input
                type="text"
                id="full_name"
                name="full_name"
                value={full_name}
                onChange={onChange}
                placeholder="Your full name"
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={phone}
                onChange={onChange}
                placeholder="+977-XXXXXXXXXX"
                className="form-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="password">Password *</label>
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={onChange}
                required
                minLength="6"
                placeholder="Min 6 characters"
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password *</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={confirmPassword}
                onChange={onChange}
                required
                minLength="6"
                placeholder="Confirm password"
                className="form-input"
              />
            </div>
          </div>

          <button type="submit" className="auth-button" disabled={loading}>
            {loading ? 'Creating Account...' : 'Sign Up'}
          </button>
        </form>

        <div className="auth-footer">
          <p className="auth-switch">
            Already have an account? <Link to="/login" state={location.state}>Login here</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Signup;
